import './app.css';

import Login from './Login';
import LoggedInNavbar from './LoggedInNavbar';
import TopTracks from './pages/TopTracks';
import TopArtists from './pages/TopArtists';

import { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';

const code = new URLSearchParams(window.location.search).get("code");

function App() {

  const [userCode, setUserCode] = useState(code);

  useEffect(() => {
    const data = window.localStorage.getItem("USER_SPOTIFY_CODE");
    if (data != null) setUserCode(data);
  }, []);

  useEffect(() => {
    window.localStorage.setItem("USER_SPOTIFY_CODE", userCode);
  }, [userCode]);

  return (
    <div>
      {code ? <LoggedInNavbar code={code} /> : <Login />}
      <Routes>
        <Route path="/" element={<div>Hi</div>} ></Route>
        <Route path="/top-tracks" element={<TopTracks code={code} />} ></Route>
        <Route path="/top-artists" element={<TopArtists code={code} />} ></Route>
      </Routes>
    </div>
  )

}

export default App;
