import React from 'react';
import useAuth from './useAuth';
import SpotifyWebApi from 'spotify-web-api-node';
import TopTracks from './pages/TopTracks';
import TopArtists from './pages/TopArtists';

import { Link } from 'react-router-dom';
import { useState, useEffect } from 'react';

const AUTH_URL = "https://accounts.spotify.com/authorize?client_id=f1873ac026914cb6831c47fc00432b3e&response_type=code&redirect_uri=http://localhost:3000/top-tracks&scope=user-top-read"

export default function LoggedInNavbar({ code }) {

    const accessToken = useAuth(code);

    const spotifyApi = new SpotifyWebApi({
        clientId: "f1873ac026914cb6831c47fc00432b3e",
        clientSecret: "d48679c7e5b54de09d997515538f7913",
        redirectUri: "http://localhost:3000"
    });

    useEffect(() => {
        if (!accessToken) {
            return;
        } else {
            spotifyApi.setAccessToken(accessToken);
        }
    }, [accessToken]);

    return (
        <div>
            <Link to="/">Home</Link>
            <Link to="/top-tracks">Top Tracks</Link>
            <Link to="/top-artists">Top Artists</Link>
            <a href={AUTH_URL}>__TOPTRACKS__</a>
        </div>
    )
}

