import React from 'react';
import useAuth from '../useAuth';
import SpotifyWebApi from 'spotify-web-api-node';
import { useState, useEffect } from 'react';

export default function TopTracks({ accessToken }) {

    const [topTracks, setTopTracks] = useState([]);

    const spotifyApi = new SpotifyWebApi({
        clientId: "f1873ac026914cb6831c47fc00432b3e",
        clientSecret: "d48679c7e5b54de09d997515538f7913",
        redirectUri: "http://localhost:3000"
    });

    useEffect(() => {
        if (!accessToken) {
            return;
        } else {
            spotifyApi.setAccessToken(accessToken);
            spotifyApi.getMyTopTracks().then(data => {
                console.log(data);
                setTopTracks(data.body.items.map(track => {
                    return {
                        artist: track.artists[0].name,
                        title: track.name,
                        albumImageUrl: track.album.images[1].url
                    }
                }));
            }, err => {
                console.log("Something went wrong!", err);
            });
        }
    }, [accessToken]);

    return (
        <div>
            <h1>Top Tracks</h1>
        </div>
    )
}
